style.content:
:root {
  --bgcolor: white;
  --btncolor: grey;
  --textcolor: black;
}
:root.dark {
  --bgcolor: black;
  --btncolor: grey;
  --textcolor: white;
}

html, body{
  font-family: Arial, Helvetica, sans-serif;
  color: var(--textcolor);
  height: 95vh;
  height: 95dvh;
  margin: 0;
  padding: 0;
  background-color: var(--bgcolor);
}
* {
  box-sizing: border-box;
}
#addbtn{
  height: 40vw;
  width: 40vw;
  max-width: 300px;
  max-height: 300px;
  border: 2px solid var(--textcolor);
  border-radius: 50%;
  background-color: var(--btncolor);
  margin: auto;
}
#counter{
  height: 7vw;
  width: 50vw;
  border: 2px solid var(--textcolor);
  border-radius: 5px;
  margin-bottom: 10px;
  margin-top: 10px;
  text-align: center;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 7vw;
  max-width: 70%;
}

#resetbtn{
  height: 10vw;
  width: 10vw;
  border: 2px solid var(--textcolor);
  border-radius: 50%;
  background-color: var(--btncolor);
  position: absolute;
  right: 17vw;
  top: 20vh;
  max-width: 4em;
  max-height: 4em;
}
#main{
  border: 2px solid var(--textcolor);
  margin: 10px;
  padding: 10px;
  height: 100%;
  border-radius: 10px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
}
#stats{
  font-size: xx-large;
}

script.content:
let main = document.querySelector('#main')
let addbtn = document.querySelector('#addbtn')
let counter = document.querySelector('#counter')
let resetbtn = document.querySelector('#resetbtn')
let highScore = document.querySelector('#high-score')
let timesClicked = 0

let getHighScore = () => {
  return localStorage.getItem('highScore') || 0
}
highScore.innerHTML = getHighScore()

addbtn.addEventListener('click', () => {
  counter.innerHTML = timesClicked += 1
  if (timesClicked > getHighScore()) {
    localStorage.setItem('highScore', timesClicked);
    highScore.innerHTML = timesClicked
  }
  navigator.vibrate([100])
})

resetbtn.addEventListener('click', () => {
  counter.innerHTML = timesClicked = 0
    navigator.vibrate([500, 100, 500])
})

main.addEventListener('click', (ev) => {
  if (ev.target !== main) return
  document.documentElement.classList.toggle('dark')
})
